var previousElement = undefined;
var previousOccupancy = 0;
//(concentrace CO2/10^6)*(objem mistnosti)*10^3

var volume = msg.volume; //65 objem mistnosti
var maxAllowed = msg.maxAllowed;
let lastCO2Changed = undefined;

let temp = {
	thermometer: 0,
	pir: 0,
	"lux-meter": 0,
	"co2-meter": 0,
	hygrometer: 0,
	battery: 0,
	barometer: 0,
	occupancy: 0,
	preference_day_temperature: 0,
	preference_night_temperature: 0,
	"voc-lp-sensor": 0,
};

function calculateOccupancy(
	previousOccupancy,
	actualCo2,
	LastCo2,
	timeDifference
) {
	return (
		previousOccupancy +
		(actualCo2 - LastCo2 - previousOccupancy * 0.0052 * timeDifference) /
			(0.0052 * timeDifference)
	);
}

function groupBy(objectArray, property) {
	return objectArray.reduce(function (acc, obj) {
		var key = obj[property];
		if (!acc[key]) {
			acc[key] = { ...temp };
		}

		if (obj["sensor"] == "undefined") 
		    return acc;

		if (obj["sensor"] == "co2-meter") {
			actualTime = parseInt(key);
			acc[key]["occupancy"] = calculateOccupancy(
				temp["occupancy"],
				obj["payload"] * 0.001 * volume,
				(temp["co2-meter"] || obj["payload"]) * 0.001 * volume,
				(actualTime - (lastCO2Changed || actualTime - 1)) / 1000
			);
			if (acc[key]["occupancy"] < 0) acc[key]["occupancy"] = 0;
			lastCO2Changed = actualTime;
		}
		acc[key][obj["sensor"]] = obj["payload"];
		temp = acc[key];
		return acc;
	}, {});
}

groupBy(msg.defaultState, "time_stamp");

return {
	...msg,
	payload: groupBy(msg.payload, "time_stamp"),
};

function getFeelTemperature(t, h, o, voc) {
	var vocp = voc / 5500;
	var res =
		t +
		0.33 *
			(h * 6.105 * Math.pow(2.718, (17.27 * t) / (237.7 + t)) + 1 * (o + vocp));
	return res;
}

function setThermostat(measured, feel, preference) {
	if (feel < preference) {
		return measured + preference - feel;
	}
	return NaN;
}

let feelTemperature = [];
let temperature = [];
let preference = [];
let termostat = [];

for (const [key, v] of Object.entries(msg.payload)) {
	if (!v) continue;

	occupancy = v["occupancy"] / msg.maxAllowed

	let feel = getFeelTemperature(
		v["thermometer"],
		v["hygrometer"] / 100,
		occupancy,
		v["voc-lp-sensor"]
	);

	var hours = new Date(parseInt(key)).getHours();
	var property =
		hours < 20 && hours > 6
			? "preference_day_temperature"
			: "preference_night_temperature";

	feelTemperature.push({
		x: parseInt(key),
		y: feel,
	});

	temperature.push({
		x: parseInt(key),
		y: v["thermometer"],
	});

	preference.push({
		x: parseInt(key),
		y: v[property],
	});

	termostat.push({
		x: parseInt(key),
		y: setThermostat(v["thermometer"], feel, v[property]),
	});
}

return {
	payload: [
